mapper.select:
SELECT
date_format(t1.bdate,'%Y-%m-%d') as bdate, t1.btype,t1.bmoney,t1.bstatus as status,t2.ename,t1.eid,t1.bid,t1.remark
count(*) as sum
FROM bill t1,employee t2 WHERE t1.eid = t2.eid
and t1.bstatus = #{status}
limit ${startNum},${pageLength}

SELECT
date_format(t1.srdata,'%Y-%m-%d') as srdata, t1.srid,t1.eid,t1.srstatus as status,t2.ename,t2.eaccount,t1.count,t1.money,t1.month,t1.filePath,t1.remark
count(*) as sum
FROM salary_record t1,employee t2 WHERE t1.eid = t2.eid
and t1.srstatus = #{status}
limit ${startNum},${pageLength}

SELECT
date_format(t1.rdate,'%Y-%m-%d') as rdate, t1.rid,t1.eid,t1.rstatus as status,t2.ename,t2.eaccount,t1.rprofit,t1.rbatch,t1.filePath,t3.pname,t1.remark
count(*) as sum
FROM restock_record t1,employee t2,provider t3 WHERE t1.eid = t2.eid AND t3.pid = t1.pid
and t1.rstatus = #{status}
limit ${startNum},${pageLength}

select t1.*
from good t1
where t1.gname=#{gname}

select t1.gkid
from good_kind t1
where t1.gkname=#{type}
