mapper.select:
SELECT
date_format(t1.bdate,'%Y-%m-%d') as bdate, t1.btype,t1.bmoney,t1.bstatus as status,t2.ename,t1.eid,t1.bid
count(*) as sum
FROM bill t1,employee t2 WHERE t1.eid = t2.eid
and t1.bstatus = #{status}
limit ${startNum},${pageLength}

SELECT
date_format(t1.srdata,'%Y-%m-%d') as srdata, t1.srid,t1.eid,t1.srstuatus as status,t2.ename,t2.eaccount,t1.count,t1.money,t1.month,t1.filePath
count(*) as sum
FROM salary_record t1,employee t2 WHERE t1.eid = t2.eid
and t1.srstuatus = #{status}
limit ${startNum},${pageLength}

SELECT
date_format(t1.rdate,'%Y-%m-%d') as rdate, t1.rid,t1.eid,t1.rstatus as status,t2.ename,t2.eaccount,t1.rprofit,t1.rbatch,t1.filePath,t3.pname
count(*) as sum
FROM restock_record t1,employee t2,provider t3 WHERE t1.eid = t2.eid AND t3.pid = t1.pid
and t1.rstatus = #{status}
limit ${startNum},${pageLength}

SELECT
date_format(t1.wdate,'%Y-%m-%d') as wdate, t1.wtype,t1.filePath,t1.wstatus as status,t2.ename,t1.eid,t1.wid
count(*) as sum
FROM waitconfirm t1,employee t2 WHERE t1.eid = t2.eid
and t1.bstatus = #{status}
limit ${startNum},${pageLength}
